import React from 'react';
import { withFormik } from 'formik';
import * as Yup from 'yup';

const formikEnhancer = withFormik({
    validationSchema: Yup.object().shape({
        firstName: Yup.string()
                .min(2, "C'mon, your name is longer than that")
                .required('First name is required.'),
        lastName: Yup.string()
                .min(2, "C'mon, your name is longer than that")
                .required('Last name is required.'),
        email: Yup.string()
                .email('Invalid email address')
                .required('Email is required!'),
    }),

    mapPropsToValues: ({ user }) => ({
        ...user,
    }),
    handleSubmit: (payload, { setSubmitting }) => {
        alert(payload.email);
        setSubmitting(false);
    },
    displayName: 'MyForm',
});
const InputFeedback = ({ error }) =>
        error ? <div className="input-feedback">{error}</div> : null;

const Label = ({ error, className, children, ...props }) => {
    return (
            <label className="label" {...props}>
                {children}
            </label>
    );
};

class Miniformik extends React.Component{
    state={
        values: this.props.initialValues || {},
        touched: {},
        errors: { }
    };


    handleChange = event => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        event.persist();
        this.setState(prevState => ({
            values: {
                ...prevState,
                [name]: value,

                },
        }));
    };

    handleBlur = event => {
        const target = event.target;
        const name = target.name;
        event.persist();
        this.setState(prevState => ({
            touched: {
                ...prevState.touched,
                [name]: true,

            },
        }));
    };
    onSubmit = e  => {
        e.preventDefault();
        // validate
      this.props.onSubmit(this.state.values);
    };

    render() {
        return this.props.children({
            ...this.state,
        handleChange: this.handleChange,
        handleBlur: this.handleBlur,
            onSubmit: this.onSubmit,
        });
    }
}

class Reactcontactform extends React.Component {



    render() {
        return (
                <Miniformik
                        initialValues={{
                    isGoing: true,
                    numberOfGuests: 2
                }}
                onSubmit={values => alert(JSON.stringify(values,null, 2 ))}>

{/* you pass a callback function to children or another prop and it gets executed during render*/}
                    { props => {
                        const { values, errors, touched, handleBlur, handleChange, onSubmit} = props;
                        return(
                                <Form>
                                    <div className="row">
                                        <div className="col">
                                            <Field name="name" type="text" />
                                        </div>
                                        <div className="col">
                                            <Field  name="email" type="email"/>
                                        </div>
                                        <div className="col">
                                            <button type="submit" disabled={onSubmitting}>verzend</button>
                                        </div>
                                    </div>
                                    <br/>

                                    <pre> { JSON.stringify(props,null, 2 )}</pre>
                                </Form>
                        );
                    }
                    }
                </Miniformik>
        );
    }
}

export default Reactcontactform;
