import React, {Component} from 'react';
import Contact from "../../pages/Contact/Contact";

class Miniformik extends React.Component{
    state={
        values: this.props.initialValues || {},
        touched: {},
        errors: { }
    };


    handleChange = event => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
        event.persist();
        this.setState(prevState => ({
            values: {
                ...prevState,
                [name]: value,

                },
        }));
    };

    handleBlur = event => {
        const target = event.target;
        const name = target.name;
        event.persist();
        this.setState(prevState => ({
            touched: {
                ...prevState.touched,
                [name]: true,

            },
        }));
    };
    onSubmit = e  => {
        e.preventDefault();
        // validate
      this.props.onSubmit(this.state.values);
    };

    render() {
        return this.props.children({
            ...this.state,
        handleChange: this.handleChange,
        handleBlur: this.handleBlur,
            onSubmit: this.onSubmit,
        });
    }
}

class Reactcontactform extends React.Component {



    render() {
        return (
                <Miniformik
                        initialValues={{
                    isGoing: true,
                    numberOfGuests: 2
                }}
                onSubmit={values => alert(JSON.stringify(values,null, 2 ))}>

// you pass a callback function to children or another prop and it gets executed during render
                    { props => {
                        const { values, errors, touched, handleBlur, handleChange, onSubmit} = props;
                        return(
                                <form onSubmit ={onSubmit}>
                                    <label>
                                        Is going:
                                        <input
                                                name="isGoing"
                                                type="checkbox"
                                                checked={values.isGoing}
                                                onChange={handleChange}
                                                onblur={handleBlur}
                                        />
                                    </label>
                                    <br/>
                                    <label>
                                        Number of guests:
                                        <input
                                                name="numberOfGuests"
                                                type="number"
                                                value={values.numberOfGuests}
                                                onChange={handleChange}/>
                                    </label>
                                    <pre> { JSON.stringify(props,null, 2 )}</pre>
                                </form>
                        );
                    }
                    }
                </Miniformik>
        );
    }
}

export default Reactcontactform;
