import React, {Component} from 'react';
import Contact from "../../pages/Contact/Contact";

class Miniformik extends React.Component{
    state={};
    render() {
        return this.props.children({...this.state});
    }
}

class Reactcontactform extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isGoing: true,
            numberOfGuests: 2
        };

        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    render() {
        return (
                <Miniformik>

// you pass a callback function to children or another prop and it gets executed during render
                    {() =>
                            <form>
                                <label>
                                    Is going:
                                    <input
                                            name="isGoing"
                                            type="checkbox"
                                            checked={this.state.isGoing}
                                            onChange={this.handleInputChange}/>
                                </label>
                                <br/>
                                <label>
                                    Number of guests:
                                    <input
                                            name="numberOfGuests"
                                            type="number"
                                            value={this.state.numberOfGuests}
                                            onChange={this.handleInputChange}/>
                                </label>
                            </form>
                    }
                </Miniformik>
        );
    }
}

export default Reactcontactform;
